var $ = require('jquery');

module.exports=  function(){
    $(function(){
        var $container= $('<div>').attr('class','app');

        var $table=$('<table>');
        var $row=$('<tr>');
        var $cell = $('<td>');

        var size=8;

        for (var i =0;i<size;i++){

            for (var j=0;j<size;j++){
                 $row.append($cell);
            }
            $table.append($row);
        }
        $container.append($table);

        $('body').append($container);

    });
};